mapper.select:
select id, pCode, pName, pImg1, pState, pPrice, pDc from product
where pName like concat ('%', #{keyword}, '%')
order by id desc limit #{skip}, #{amount}

mapper.update:
update product set pState=#{pState}, pName=#{pName}, pPrice=#{pPrice}, pDc=#{pDc}, pMile=#{pMile},
pInven=#{pInven}, pSize=#{pSize}, pImg1=#{pImg1}, pImg2=#{pImg2}, pImg3=#{pImg3}
where pCode=#{pCode}

mapper.select:
select * from order1
where ${type} like concat ('%', #{keyword}, '%')
order by id desc limit #{skip}, #{amount}
